mapper.select:
select 

from aldstat_daily_link_monitor
where id = #{id,jdbcType=BIGINT}

SELECT ana.open_count AS total,ald.open_count AS extend ,ana.open_count-ald.open_count AS naturalquantity
FROM
(select app_key,open_count,day
from
aldstat_daily_link_monitor
where day = #{date}
and app_key=#{appkey}
and wsr_query_ald_link_key='all'
and wsr_query_ald_media_id='all'
and ag_ald_position_id='all'
and hour='all'
)  ald
INNER JOIN (
SELECT open_count,app_key,day FROM aldstat_trend_analysis
WHERE day=#{date} and app_key=#{appkey}
) ana
ON ald.app_key=ana.app_key AND ald.day=ana.day
WHERE ald.app_key=#{appkey} and ald.day= #{date}

SELECT ana.hour,ana.app_key,ana.open_count AS total,ald.open_count AS extend ,ana.open_count-ald.open_count AS naturalquantity
FROM
(select app_key,open_count,`hour`
from
aldstat_daily_link_monitor
where day=#{date}
AND app_key=#{appkey}
and wsr_query_ald_link_key='all'
and wsr_query_ald_media_id='all'
and ag_ald_position_id='all'
and `hour`!='all'
)  ald
INNER JOIN (
SELECT * FROM aldstat_hourly_trend_analysis
WHERE day=#{date}
AND app_key=#{appkey}
) ana
ON ald.app_key=ana.app_key AND ald.hour=ana.hour

