select *
FROM
aldstat_daily_link_monitor
where `day`=#{date}
 and app_key=#{appkey} 
 and ag_ald_position_id=#{scene} 
and wsr_query_ald_link_key = 'all'
and ag_ald_position_id != 'all'
and wsr_query_ald_media_id = 'all'
and hour='all'


        order by ${prop} ${order}

SELECT mo.* from
( select *
FROM
aldstat_daily_link_monitor
where `day`=#{date}
and app_key=#{appkey}
and wsr_query_ald_link_key != 'all'
and ag_ald_position_id = 'all'
and wsr_query_ald_media_id != 'all'
and hour!='all') mo INNER JOIN ald_link_trace ce on mo.wsr_query_ald_link_key=ce.link_key INNER JOIN ald_media me on mo.wsr_query_ald_media_id=me.media_id
 where me.media_name = #{channel} 
 where ce.link_name = #{activitiesName} 
order by mo.hour desc

  SELECT mo.*,ce.link_name FROM(select *
  FROM
  aldstat_daily_link_monitor
  where `day`=#{date}
   and app_key=#{appkey} 
  and wsr_query_ald_link_key != 'all'
 and ag_ald_position_id=#{scene} 
  and ag_ald_position_id != 'all'
  and wsr_query_ald_media_id = 'all'
and hour='all') mo INNER JOIN ald_link_trace ce on mo.wsr_query_ald_link_key=ce.link_key
   where ce.link_name like CONCAT('%',#{activitiesName},'%') 


          order by mo.${prop} ${order}

