select a.app_key, a.day, a.hour, b.open_count as open_total_Count, b.new_comer_count as new_total_Count, b.visitor_count as visitor_total_Count, a.link_open_count as open_popu_Count, a.link_newer_for_app as new_popu_Count, a.link_visitor_count as visitor_popu_Count, (b.open_count - a.link_open_count) as open_natu_count, (b.new_comer_count - a.link_newer_for_app) as new_natu_count, (b.visitor_count - a.link_visitor_count) as visitor_natu_count FROM (select app_key, day, hour, link_open_count, link_newer_for_app, link_visitor_count from aldstat_link_summary_hour where 1=1
and `day` = #{date}
and `app_key` = #{appKey}
) a JOIN (select app_key, day, hour, open_count, new_comer_count, visitor_count from aldstat_hourly_trend_analysis where 1=1
and `day` = #{date}
and `app_key` = #{appKey}
) b ON a.app_key=b.app_key and a.day=b.day and a.hour=b.hour

select a.app_key, a.day, b.open_count as open_total_Count, b.new_comer_count as new_total_Count, b.visitor_count as visitor_total_Count, a.link_open_count as open_popu_Count, a.link_newer_for_app as new_popu_Count, a.link_visitor_count as visitor_popu_Count, (b.open_count - a.link_open_count) as open_natu_count, (b.new_comer_count - a.link_newer_for_app) as new_natu_count, (b.visitor_count - a.link_visitor_count) as visitor_natu_count FROM (select app_key, day, link_open_count, link_newer_for_app, link_visitor_count from ${tableName1} where 1=1
and `day` = #{date}
and `app_key` = #{appKey}
) a JOIN (select app_key, day, open_count, new_comer_count, visitor_count from ${tableName2} where 1=1
and `day` = #{date}
and `app_key` = #{appKey}
) b ON a.app_key=b.app_key and a.day=b.day

select a.app_key, a.day, a.open_count as open_total_Count, a.new_comer_count as new_total_Count, a.visitor_count as visitor_total_Count, b.link_open_count as open_popu_Count, b.link_newer_for_app as new_popu_Count, b.link_visitor_count as visitor_popu_Count, (a.open_count - b.link_open_count) as open_natu_count, (a.new_comer_count - b.link_newer_for_app) as new_natu_count, (a.visitor_count - b.link_visitor_count) as visitor_natu_count from (select app_key, `day`, sum(open_count) as open_count, sum(new_comer_count) as new_comer_count, sum(visitor_count) as visitor_count from aldstat_trend_analysis where 1=1
and `day` in
#{list1}
and `app_key` = #{appKey}
